import React, { useEffect, useState } from "react";
import "./PhotoGrid.css";
import { FaHeart } from "react-icons/fa";
import axios from "axios";

const PhotoGrid = ({ photos }) => {
    const [likedPhotos, setLikedPhotos] = useState({});

    const toggleLike = (photoId, url) => {
        console.log(photoId, url)
        setLikedPhotos((prevLikedPhotos) => {
            const newState = {
                ...prevLikedPhotos,
                [photoId]: { photoId, url }
            };
            return newState;
        });
        // setLikedPhotos((prevLikedPhotos) => ({
        //     ...prevLikedPhotos,
        //     [photoId]: prevLikedPhotos[photoId]
        //         ? { ...prevLikedPhotos[photoId], liked: !prevLikedPhotos[photoId].liked }
        //         : { url, liked: true }
        // }));
    };
    // [備忘録]stateの変更を検知してからuseEffectするようにしたらうまく最新のstateを取得できた
    useEffect(() => {
        const saveInterval = setInterval(() => {
            // console.log("最新の likedPhotos:", likedPhotos); // ここで最新の状態をログ出力
            saveLikedPhotosStatus();
        }, 5000);

        return () => clearInterval(saveInterval);
    }, [likedPhotos]);
    const saveLikedPhotosStatus = async () => {
        const username = "test"
        const likedPhotostest = {
            "abc12345": { photoId: "abc1234", url: "https://example.com/photo1.jpg" }
        };
        console.log("最新のstate", likedPhotos)
        const photosArray = Object.entries(likedPhotostest).map(([photoId, data]) => ({
            photoId,
            url: data.url,
            liked: true
        }));
        // const requestBody = JSON.stringify({
        //     username,
        //     likedPhotoStatus: photosArray
        // })
        try {
            // console.log("配列:", photosArray)
            const response = await axios.post("http://localhost:8080/registerPhoto", {
                username,
                likedPhotoStatus: photosArray
            })
        } catch (error) {
            console.error("CannotRegisterLikedPhoto", error)
        }
    }

    // const saveLikedPhotosStatus = async () => {
    //     const username = "test"
    //     const photosArray = Object.entries(likedPhotos).map(([photoId, data]) => ({
    //         photoId,
    //         url: data.url,
    //         liked: data.liked,
    //     }));
    //     // const requestBody = JSON.stringify({
    //     //     username,
    //     //     likedPhotoStatus: photosArray
    //     // })
    //     try {
    //         const response = await axios.post("http://localhost:8080/registerPhoto", {
    //             username,
    //             likedPhotoStatus: photosArray
    //         })
    //     } catch (error) {
    //         console.error("CannotRegisterLikedPhoto", error)
    //     }
    // }
    return (
        <div className="photo-grid">
            {photos.map((photo) => (
                <div key={photo.id} className="photo-item">
                    <img src={photo.urls.regular} />
                    {/* <img src={photo} /> */}
                    <div
                        className={`like-icon ${likedPhotos[photo.id] ? "liked" : ""}`}
                        onClick={() => toggleLike(photo.id, photo.urls.regular)}
                    >
                        <FaHeart />
                    </div>
                </div>
            ))}
        </div>
    );
};

export default PhotoGrid;
