import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./LoginForm.css";
import axios from "axios";
import HamburgerMenu from "../components/HamburgerMenu";
const LoginForm = () => {
  const API_BASE_URL = process.env.REACT_APP_GO_BASE_URL;
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const responce = await axios.post(`${API_BASE_URL}/login`, {
        username,
        password
      }, {
        withCredentials: true
      })
      setError("")
      console.log(document.cookie);
      responce = await axios.get(`${API_BASE_URL}/home`, {
        withCredentials: true
      })
      navigate("/home", { state: { username: username } });
    } catch (error) {
      setError("Login failed")
    }
  };

  return (
    <div>
      <HamburgerMenu menu1="Login" menu2="Sign in" href1="/" href2="/register" />
      <div className="login-container">
        <div className="container" >
          <h2>ログイン</h2>
          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter your username"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            {error && <p className="error-message">{error}</p>}
            <button type="submit" className="login-button">Login</button>
          </form>
          <div className="registerLink">
            <Link to="/register" className="link"> 新規登録はこちらから</Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
