import { useState } from "react";
import "./LikedPhotosGrid.css"
import axios from "axios";
const API_BASE_URL = process.env.REACT_APP_GO_BASE_URL;
const LikedPhotosGrid = ({ photos, username, setPhotos }) => {
    const [selectedPhoto, setSelectedPhoto] = useState(null);

    const handlePhotoClick = (photo) => {
        setSelectedPhoto(photo);
    };
    const toggleLike = (photoId) => {
        setPhotos((prevPhotos) => prevPhotos.filter(photo => photo.Id !== photoId));
    };

    const removeLike = async (event) => {
        event.stopPropagation();
        if (!selectedPhoto) return;
        try {
            await axios.post(`${API_BASE_URL}/deleteUnlikedPhoto`, {
                username,
                photoId: selectedPhoto.Id
            }, { withCredentials: true });
            setSelectedPhoto(null);
            toggleLike(selectedPhoto.Id)
        } catch (error) {
            console.error("いいね解除に失敗しました", error);
        }
    };
    return (
        <div className="photo-grid">
            {photos.map((photo) => (
                <div key={photo.Id} className="photo-item"
                    onClick={() => handlePhotoClick(photo)}
                    style={{ position: "relative" }} >
                    <img src={photo.Url.Regular} />
                    {selectedPhoto?.Id === photo.Id && (
                        <div className="unlike-modal">
                            <p>いいねを外しますか？</p>
                            <button onClick={(e) => { removeLike(e) }}>いいねを解除</button>
                            <button onClick={(e) => { e.stopPropagation(); setSelectedPhoto(null); }}>キャンセル</button>                        </div>
                    )}
                </div>
            ))}
        </div>

    );
};
export default LikedPhotosGrid;
