import { useState } from "react";
import "./LikedPhotosGrid.css"
import axios from "axios";
const API_BASE_URL = process.env.REACT_APP_GO_BASE_URL;
const LikedPhotosGrid = ({ photos, username }) => {
    const [selectedPhoto, setSelectedPhoto] = useState(null); // ✅ 選択された写真を管理

    // ✅ クリック時に選択した写真をセット（アクションボタン表示用）
    const handlePhotoClick = (photo) => {
        setSelectedPhoto(photo);
    };

    // ✅ いいね解除ボタンを押したらバックエンドに削除リクエストを送信
    const removeLike = async () => {
        if (!selectedPhoto) return;

        try {
            await axios.post(`${API_BASE_URL}/unlikePhoto`, {
                username,
                photoId: selectedPhoto.Id
            }, { withCredentials: true });

            // ✅ 成功したら `selectedPhoto` をリセット（ボタンを非表示）
            setSelectedPhoto(null);
        } catch (error) {
            console.error("いいね解除に失敗しました", error);
        }
    };
    return (
        <div className="photo-grid">
            {photos.map((photo) => (
                <div key={photo.Id} className="photo-item" onClick={handlePhotoClick}>
                    <img src={photo.Url.Regular} />
                    {selectedPhoto?.Id === photo.Id && (
                        <div className="unlike-modal">
                            <p>いいねを外しますか？</p>
                            <button onClick={removeLike}>いいねを解除</button>
                            <button onClick={() => setSelectedPhoto(null)}>キャンセル</button>
                        </div>
                    )}

                </div>
            ))}
        </div>

    );
};
export default LikedPhotosGrid;
