import axios from "axios";
import HamburgerMenu from "../components/HamburgerMenu";
import PhotoGrid from "../components/PhotoGrid";
import "./Home.css";
import { useEffect, useState } from "react";
import { Title } from "../components/Title";
import { useLocation } from "react-router-dom";
const Home = () => {
    const [photo, setPhoto] = useState();
    const [reload, setReload] = useState(false);
    const location = useLocation();
    // LoginFormからusernameを取得する
    const username = location.state?.username || "Guest";
    const handleReload = () => {
        setReload(prev => !prev);
    };
    const apikey = "sWZdNezChJpcagD8SZzmCw42Ifnad7XS6cyaB4Nsm6Q"
    const fetchPhoto = () => {
        axios
            .get(
                `https://api.unsplash.com/photos/random?query=sky&count=12&client_id=${apikey}`
            )
            .then((res) => {
                if (JSON.stringify(photo) !== JSON.stringify(res.data)) {
                    setPhoto(res.data);
                    console.log(res.data);
                }
            });
    };
    useEffect(() => {
        fetchPhoto();
    }, [reload]);
    useEffect(() => {
        fetchPhoto();
    }, []);
    // const photo = ["https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D", "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D",
    //     "https://images.unsplash.com/photo-1523380360198-653413ad178b?w=800&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1yZWxhdGVkfDJ8fHxlbnwwfHx8fHw%3D"
    // ]
    return (
        <div className="app-container">
            <div className="sidebar">
                <HamburgerMenu menu1="MyPage" menu2="Logout" href1="/user/mypage" href2="/" />
            </div>
            {/* <div className="title">
                <Title sentence="お気に入りの空を見つけよう！" />
            </div> */}
            <div className="reload">
                <button className="reloadButton" onClick={handleReload}>更新する</button>
            </div>
            <div className="content">
                {photo && < PhotoGrid photos={photo} username={username} />}
            </div>
        </div>
    );
};
export default Home;
