import React, { useEffect, useState } from "react";
import "./PhotoGrid.css";
import { FaHeart } from "react-icons/fa";
import axios from "axios";

const PhotoGrid = ({ photos, username }) => {
    const API_BASE_URL = process.env.REACT_APP_GO_BASE_URL;

    const [likedPhotos, setLikedPhotos] = useState({});

    const toggleLike = (photoId, url) => {
        setLikedPhotos((prevLikedPhotos) => {
            const newState = {
                ...prevLikedPhotos,
                [photoId]: { photoId, url }
            };
            return newState;
        });
    };
    // [備忘録]stateの変更を検知してからuseEffectするようにしたらうまく最新のstateを取得できた
    useEffect(() => {
        const saveInterval = setInterval(() => {
            saveLikedPhotosStatus();
        }, 1000);
        return () => clearInterval(saveInterval);
    }, [likedPhotos]);
    const saveLikedPhotosStatus = async () => {
        console.log("最新のstate", likedPhotos)
        const photosArray = Object.entries(likedPhotos).map(([photoId, data]) => ({
            photoId,
            url: data.url,
            liked: true
        }));
        console.log("photosArray", photosArray)
        try {
            const response = await axios.post(`${API_BASE_URL}/registerPhoto`, {
                username,
                likedPhotoStatus: photosArray
            }, {
                withCredentials: true
            })
        } catch (error) {
            console.error("CannotRegisterLikedPhoto", error)
        }
    }
    return (
        <div className="photo-grid">
            {photos.map((photo) => (
                <div key={photo.id} className="photo-item">
                    <img src={photo.urls.regular} />
                    {/* <img src={photo} /> */}
                    <div
                        className={`like-icon ${likedPhotos[photo.id] ? "liked" : ""}`}
                        onClick={() => toggleLike(photo.id, photo.urls.regular)}
                    >
                        <FaHeart />
                    </div>
                </div>
            ))}
        </div>
    );
};

export default PhotoGrid;
