import axios from "axios";
import HamburgerMenu from "../components/HamburgerMenu";
import PhotoGrid from "../components/PhotoGrid";
import "./Home.css";
import { useEffect, useState } from "react";
import { useGetUserInfo } from "../hooks/useGetUserInfo";
const Home = () => {
    const [photo, setPhoto] = useState();
    const [reload, setReload] = useState(false);
    const handleReload = () => {
        setReload(prev => !prev);
    };
    const username = useGetUserInfo();
    const apikey = process.env.REACT_APP_UNSPLASH_API_KEY;
    const hostname = process.env.REACT_APP_GO_BASE_URL
    const fetchPhoto = () => {
        axios
            .get(
                `https://api.unsplash.com/photos/random?query=sky&count=21&client_id=${apikey}`
            )
            .then((res) => {
                if (JSON.stringify(photo) !== JSON.stringify(res.data)) {
                    setPhoto(res.data);
                    console.log(res.data);
                }
            });
    };
    useEffect(() => {
        fetchPhoto();
    }, [reload]);
    useEffect(() => {
        fetchPhoto();
    }, []);
    return (
        <div className="app-container">
            <div className="sidebar">
                <HamburgerMenu menu1="MyPage" menu2="Logout" link1="/mypage" link2="/logout" username={username} />
            </div>
            <div className="reload">
                <button className="reloadButton" onClick={handleReload}>更新する</button>
            </div>
            <div className="content">
                {photo && < PhotoGrid photos={photo} />}
            </div>
        </div>
    );
};
export default Home;
