import React, { useState } from "react";
import HamburgerMenu from "../components/HamburgerMenu";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./RegisterPage.css"
export const RegisterPage = () => {
    const API_BASE_URL = process.env.REACT_APP_GO_BASE_URL;
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let isLogined = await axios.post(`${API_BASE_URL}/register`, {
                username,
                password
            })
            setError("")
            alert("ユーザー登録が完了しました")
            navigate("/home");
        } catch (error) {
            setError("既にそのユーザー名は使用されています。")
        }
    };

    return (
        <div>
            <HamburgerMenu menu1="Login" menu2="Sign in" href1="/" href2="/register" />
            <div className="register-container">
                <div className="container" >
                    <h2>新規登録</h2>
                    <form onSubmit={handleSubmit} className="register-form">
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input
                                type="text"
                                id="username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                placeholder="Enter your username"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Enter your password"
                                required
                            />
                        </div>
                        {error && <p className="error-message">{error}</p>}
                        <button type="submit" className="register-button">上記の内容で登録</button>
                    </form>
                </div>
            </div>
        </div>
    );
};
